package Unary_Operator;

public class Operator_1 {
	// TODO Auto-generated method stub
	public static void main(String[] args) {

	int a=15;
	int b=a;
	System.out.println("a : "+a);
	System.out.println("b : "+b);
 
	int k=++a;
	System.out.println("a : "+a);
	System.out.println("k : "+k);

	int j=k++;
	System.out.println("k : "+k);
	System.out.println("j : "+j);
	}


}
