package Unary_Operator;

public class Operator_2 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=54;
System.out.println("x :"+ x++);
System.out.println("x : "+ x);
System.out.println("x : "+ ++x);
System.out.println("x : "+ x);

int y=26;
System.out.println("y : "+ y--);
System.out.println("y : "+ y);
System.out.println("y : "+ --y);
System.out.println("y : "+ y);

int z=12;
System.out.println("z : "+ z++);
System.out.println("z : "+ z);
System.out.println("z : "+ --z);
System.out.println("z : "+ z);
	}

}
