package loops;

public class whileloop {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=5;
while(a>0) {
	a--;
	System.out.println(a);
}
System.out.println("****letters****");
char c1='a';
while(c1<='z') {
	System.out.print(c1);
	c1++;
	
}
System.out.println("\n*****reverse****");
 char v1='z';
while(v1>='a') {
	System.out.print(v1);
	v1--;
	
}

}
}
